import codonOpt
import logging


seq = codonOpt.DNA("")

codon_table_source = codonOpt.CodonTable(json_file='Data/Tth codon table.json')
seq.get_frequencies(codon_table_source)

codon_table_destination = codonOpt.CodonTable(json_file='Data/Escherichia_coli_K12.json')
seq_gen = codonOpt.Sequence_Generator(codon_table_destination)

new_seq = seq_gen.match_codon_usage(seq)
new_seq.get_frequencies(codon_table_destination)

print('Original: ' + str(seq.freq))
print('New Seq : ' + str(new_seq.freq))
print('DNA Seq: ' + str(new_seq))




